targets.expr:
round(
  sum by (le) (
    avg by(le, ic_subnet) (
        increase(
          unstable_blocks_tip_depths_bucket{ic="$ic", job="bitcoin-$network-canister"}
          [$heatmap_period]
        )
    )
  )
)

round(
  sum by (le) (
    avg by(le, ic_subnet) (
      # Multiply bucket ranges by 1M to compensate for modifications on the Bitcoin canister side
      label_replace(
        increase(
          ins_get_utxos_total_bucket{ic="$ic", job="bitcoin-$network-canister"}
          [$heatmap_period]
        ),
        "le", "${1}000000", "le", "^([0-9]+)$"
      )
    )
  )
)

