targets.expr:
sum by(ic, status) (
    quantile by(ic, ic_subnet, type, status) (
        0.5,
        rate(mr_inducted_xnet_message_count{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance"}[$period])
    )
)

sum by (le) (
  quantile without(ic_node, instance) (
    0.5,
    sum without(remote) (
      increase(mr_time_in_stream_bucket{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance"}[$heatmap_period])
    )
  )
)

sum by (le) (
  quantile without(ic_node, instance) (
    0.5,
    sum without(remote) (
      increase(mr_time_in_backlog_bucket{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance"}[$heatmap_period])
    )
  )
)

sum by (le) (
  avg by(le, ic_subnet) (
    increase(xnet_endpoint_response_size_bytes_bucket{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance",resource="stream"}[$heatmap_period])
  )
)
-
sum by (le) (
  avg by(le, ic_subnet) (
    increase(xnet_builder_response_body_size_bytes_bucket{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance",status="success"}[$heatmap_period])
  )
)
