targets.expr:
label_replace(
  label_replace(
      topk(20,
        (
            node_filesystem_size_bytes{device="/dev/mapper/store-shared--data", ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance", mountpoint="/var/lib/ic/data"}
          -
            node_filesystem_free_bytes{device="/dev/mapper/store-shared--data", ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance", mountpoint="/var/lib/ic/data"}
        )
      /
        node_filesystem_size_bytes{device="/dev/mapper/store-shared--data", ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance", mountpoint="/var/lib/ic/data"}
      ),
      "instance", "$1:...:$2", "instance", "(\\[(?:[^:]+[.:]){4}).*(:[^:]+\\]?:[0-9]+)"
  ),
  "ic_subnet", "$1", "ic_subnet", "^([a-z0-9]+)-.+"
)

label_replace(
  label_replace(
      (
        topk(20,
          node_filesystem_files{ic="$ic", ic_subnet=~"$ic_subnet", instance=~"$instance", mountpoint=~"/var/lib/ic/.+"}
          -
          node_filesystem_files_free{ic="$ic", ic_subnet=~"$ic_subnet", instance=~"$instance", mountpoint=~"/var/lib/ic/.+"}
        )
      ),
    "instance", "$1:...:$2", "instance", "(\\[(?:[^:]+[.:]){4}).*(:[^:]+\\]?:[0-9]+)"
  ),
  "ic_subnet", "$1", "ic_subnet", "^([a-z0-9]+)-.+"
)

label_replace(
  label_replace(
      (
        topk(20, max(process_open_fds{ic=~"$ic", ic_subnet=~"$ic_subnet"}) by (ic_subnet, instance, job))
      ),
    "instance", "$1:...:$2", "instance", "(\\[(?:[^:]+[.:]){4}).*(:[^:]+\\]?:[0-9]+)"
  ),
  "ic_subnet", "$1", "ic_subnet", "^([a-z0-9]+)-.+"
)
