targets.expr:
sum by(api_type) (
  quantile by(api_type, ic_subnet) (
    0.5,
    sum without(status) (
      rate(sandboxed_execution_executed_message_slices_total{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance",api_type!="non replicated query",api_type!="inspect message"}[$__rate_interval])
    )
  )
)

sum by (api_type, status) (
    quantile by (api_type, status, ic_subnet) (
        0.5,
        rate(sandboxed_execution_executed_message_slices_total{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance",api_type!="non replicated query",api_type!="inspect message",status!~"Success|NoResponse|Paused"}[$__rate_interval])
    )
)

label_replace(
  quantile by (status, ic_subnet) (
    0.5,
    sum without(api_type) (
      rate(sandboxed_execution_executed_message_slices_total{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance",api_type="non replicated query",status!~"Success|NoResponse"}[$__rate_interval])
    )
  ),
  "ic_subnet", "$1", "ic_subnet", "([a-z0-9]+)-.*"
)

