label_replace(
    quantile by (ic_subnet) ( 
        0.5,
        (
            sum without(api_type, status)(
                rate(
                    sandboxed_execution_executed_message_slices_total{ic="$ic",ic_subnet=~"$ic_subnet",job="replica",instance=~"$instance",api_type!~"non[ -]replicated.*|inspect message"}[$__rate_interval]
                )
            ) 
            - 
            rate(sandboxed_execution_spawn_process_duration_seconds_count{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance"}[$__rate_interval])
        )
        /
        sum without(api_type, status)(
            rate(
                sandboxed_execution_executed_message_slices_total{ic="$ic",ic_subnet=~"$ic_subnet",job="replica",instance=~"$instance",api_type!~"non[ -]replicated.*|inspect message"}[$__rate_interval]
            )
        )
    ),
    "ic_subnet", "$1", "ic_subnet", "([a-z0-9]+)-.*"
) * 100

