targets.expr:
sum by (le) (
      quantile by (le, ic_subnet) (
        0.5,
        storage_layer_num_merged_files_bucket{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance"} -       storage_layer_num_merged_files_bucket offset $period
    )
)

sum by (le) (
      quantile by (le, ic_subnet) (
        0.5,
        storage_layer_merge_num_files_by_shard_bucket{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance"} - storage_layer_merge_num_files_by_shard_bucket offset $period
    )
)

sum by (le) (
      quantile by (le, ic_subnet) (
        0.5,
        storage_layer_merge_storage_overhead_by_shard_bucket{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance"} - storage_layer_merge_storage_overhead_by_shard_bucket offset $period
    )
)

label_replace(
  label_replace(
      (
        topk(20,
          node_filesystem_files{ic="$ic", ic_subnet=~"$ic_subnet", instance=~"$instance", mountpoint=~"/var/lib/ic/data"}
          -
          node_filesystem_files_free{ic="$ic", ic_subnet=~"$ic_subnet", instance=~"$instance", mountpoint=~"/var/lib/ic/data"}
        )
      ),
    "instance", "$1:...:$2", "instance", "(\\[(?:[^:]+[.:]){4}).*(:[^:]+\\]?:[0-9]+)"
  ),
  "ic_subnet", "$1", "ic_subnet", "^([a-z0-9]+)-.+"
)
