targets.expr:
sum by(status, type) (
  quantile by(ic, status, ic_subnet, type) (
    0.5,
    rate(mr_inducted_xnet_message_count{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance="$instance"}[$period])
  )
)

sum by(status, type) (
  quantile by(ic, status, ic_subnet) (
    0.5,
    rate(mr_inducted_ingress_message_count{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance="$instance"}[$period])
  )
)

sum by(kind) (
  quantile by (ic, ic_subnet, kind) (
    0.5,
    execution_input_queue_messages{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance="$instance"}
  )
)

sum(
  quantile by (ic, ic_subnet) (
    0.5,
    sum without(remote, destination) (mr_stream_messages{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance="$instance"})
  )
)

sum by(api_type) (
  quantile by (ic_subnet, api_type, status) (
    0.5,
    rate(sandboxed_execution_executed_message_slices_total{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance="$instance",api_type!~"non[ -]replicated.*|inspect message"}[$__rate_interval])
  )
)

sum by(api_type) (
  quantile by (ic_subnet, api_type, status) (
    0.5,
    rate(sandboxed_execution_executed_message_slices_total{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance="$instance",api_type=~"non[ -]replicated.*|inspect message"}[$__rate_interval])
  )
)
