targets.expr:
  sum by(dc) (
    label_replace(
      rate(replica_http_request_duration_seconds_sum{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance=~"$instance",request_type=~".*/read_state"}[$period]),
      "dc", "$1", "instance", "\\[?([^:]*:[^:]*:[^:]*:[^:]*):.*"
    )
  )
/
  sum by(dc) (
    label_replace(
      rate(replica_http_request_duration_seconds_count{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance=~"$instance",request_type=~".*/read_state"}[$period]),
      "dc", "$1", "instance", "\\[?([^:]*:[^:]*:[^:]*:[^:]*):.*"
    )
  )

  sum by(dc) (
    label_replace(
      rate(replica_http_request_duration_seconds_sum{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance=~"$instance",request_type!~".*/read_state|/api/v3/canister/:effective_canister_id/call"}[$period]),
      "dc", "$1", "instance", "\\[?([^:]*:[^:]*:[^:]*:[^:]*):.*"
    )
  )
/
  sum by(dc) (
    label_replace(
      rate(replica_http_request_duration_seconds_count{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance=~"$instance",request_type!~".*/read_state|/api/v3/canister/:effective_canister_id/call"}[$period]),
      "dc", "$1", "instance", "\\[?([^:]*:[^:]*:[^:]*:[^:]*):.*"
    )
  )
