targets.expr:
sum(
quantile by (ic_subnet) (
0.5,
rate(
(
max_over_time(
artifact_pool_consensus_height_stat{
job="replica",ic=~"$ic",ic_subnet=~"$ic_subnet",type="finalization",pool_type="validated",stat="max"
}[5m]
) != 0
)[1m:10s]
)
)
)

avg(
avg_over_time(
ic_icsubnet_status:replicated_state_registered_canisters:last_over_time
{status=~"stopped", ic=~"$ic", ic_subnet="$ic_subnet"}[1h]
)
)


avg(
avg_over_time(
ic_status:replicated_state_registered_canisters:sum
{status=~"stopped", ic=~"$ic"}[1h]
)
)

sum(
avg_over_time(
ic_apitype:sandboxed_execution_executed_message_slices_total:rate1m
{api_type!~"non[ -]replicated.*", ic="$ic"}[1d]
)
) * 86400


sum(
avg_over_time(
ic_apitype:sandboxed_execution_executed_message_slices_total:rate1m
{api_type!~"non[ -]replicated.*", ic="mercury"}[1d]
)
) * 86400
